const {Packager} = require('@atlaspack/plugin');

module.exports = new Packager({
  async loadConfig({config}) {
    let res = await config.getConfig(['.packagerrc']);
    return {
      value: res && res.contents.value
    };
  },
  package({config}) {
    return {
      contents: config.value || 'packaged'
    }
  }
});
